folderPath.value:
@concat(
    if(not(empty(pipeline().Pipeline)), pipeline().Pipeline, dataset().ds_PipelineName),
    '/',
    formatDateTime(string(
        convertFromUtc(
            if(not(empty(pipeline().TriggerTime)), pipeline().TriggerTime, dataset().ds_PipelineTriggerTime), 
        'AUS Eastern Standard Time')),
    'yyyy-MM-ddTHH-mm-ss'),
    '_', 'PipelineRunId_', 
    if(not(empty(pipeline().RunId)), pipeline().RunId, dataset().ds_PipelineRunId)
)
